import { ElogConfig } from '@elog/core';
export * from '@elog/core';

declare function run(): Promise<void>;

/**
 * Type helper to make it easier to use vite.config.ts
 * accepts a direct {@link ElogConfig} object, or a function that returns it.
 */
declare function defineConfig(config: ElogConfig): ElogConfig;

export { defineConfig, run };
